// App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Registro from './Components/Registro/Registro';
import Main from './Components/Proyectos/Main';
import Login from './Pages/Login';
import Header from './Container/Header';
import './styles.css'; // Importa el archivo CSS con tus estilos personalizados

function App() {
  const [darkMode, setDarkMode] = useState(false); // Estado para el modo oscuro

  // Función para alternar entre el modo claro y oscuro
  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
    // Aquí puedes implementar la lógica para cambiar los estilos de toda la aplicación según el modo seleccionado
  };

  return (
    <Router>
      <Header darkMode={darkMode} toggleDarkMode={toggleDarkMode} />
      <div className={`App ${darkMode ? 'dark' : ''}`}>
        <div className='spacer'>

        <Routes>
          <Route path="/" element={<Registro />} /> {/* Ruta por defecto */}
          <Route path="/login" element={<Login/>} />
          <Route path="/main/:username" element={<Main/>} />
          <Route path="/register" element={<Registro/>} />
        </Routes>
          </div>
      </div>
    </Router>
  );
}

export default App;
