import React, { useState } from "react";
import ProyectoForm from "./ProyectoForm";
import EditarProyectoForm from "./EditarProyectoForm";
import "./Main.css";
import { FaEdit, FaTrash, FaPlus } from 'react-icons/fa';

const Main = () => {
  const [proyectos, setProyectos] = useState([]);
  const [mostrarFormulario, setMostrarFormulario] = useState(false);
  const [proyectoEditar, setProyectoEditar] = useState(null);

  // Función para manejar la edición de un proyecto
  const handleEditarProyecto = (proyecto) => {
    setProyectoEditar(proyecto);
    toggleFormulario();
  };

  // Función para manejar el envío del formulario de edición
  const handleEditarProyectoSubmit = async (proyectoActualizado) => {
    // Aquí puedes enviar la solicitud para actualizar el proyecto en la base de datos
    // Luego, actualiza la lista de proyectos en el estado
    console.log("Proyecto actualizado:", proyectoActualizado);
    // Por ejemplo:
    // setProyectos([...proyectos.filter(p => p.id !== proyectoActualizado.id), proyectoActualizado]);
    toggleFormulario();
  };

  // Función para mostrar/ocultar el formulario de edición
  const toggleFormulario = () => {
    setMostrarFormulario(!mostrarFormulario);
  };

  return (
    <div className="main-container">
      <div className="header">
        <h2>Proyectos</h2>
        <button className="crear-proyecto-btn" onClick={() => setMostrarFormulario(true)}>
          <FaPlus />
        </button>
      </div>

      {mostrarFormulario && (
        <div className="ventana-emergente">
          <div className="contenido">
            {proyectoEditar ? (
              <EditarProyectoForm proyectoInicial={proyectoEditar} onSubmit={handleEditarProyectoSubmit} />
            ) : (
              <ProyectoForm onSubmit={handleCrearProyecto} />
            )}
          </div>
        </div>
      )}

      <ul className="proyectos-lista">
        {proyectos.map((proyecto, index) => (
          <li className="proyecto" key={index}>
            {/* Información del proyecto */}
            <div className="proyecto-info">
              {/* Detalles del proyecto */}
            </div>
            {/* Botones de editar y eliminar */}
            <div className="botones-proyecto">
              <button className="editar-btn" onClick={() => handleEditarProyecto(proyecto)}>
                <FaEdit />
              </button>
              <button className="eliminar-btn" onClick={() => handleEliminarProyecto(proyecto.id)}>
                <FaTrash />
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Main;
