import React, { useState, useEffect } from "react";
import Swal from 'sweetalert2';
import { FaEdit, FaPlus, FaTimes, FaTrash } from 'react-icons/fa';
import ProyectoForm from "./ProyectoForm";
import ProyectoEdit from "./ProyectoEdit";

const Main = () => {
  const [proyectos, setProyectos] = useState([]);
  const [mostrarFormulario, setMostrarFormulario] = useState(false);
  const [proyectoEditar, setProyectoEditar] = useState(null);

  useEffect(() => {
    getAllProject();
  }, []);

  const getAllProject = async () => {
    try {
      const response = await fetch("http://localhost:3000/projects/");
      if (response.ok) {
        const data = await response.json();
        setProyectos(data);
      } else {
        throw new Error("Error al obtener los proyectos: " + response.statusText);
      }
    } catch (error) {
      console.error("Error al comunicarse con el servidor:", error);
      Swal.fire({
        title: 'Error',
        text: 'Hubo un problema al cargar los proyectos. Por favor, intenta nuevamente más tarde.',
        icon: 'error',
        confirmButtonText: 'Aceptar'
      });
    }
  };

  const handleCrearProyecto = () => {
    setProyectoEditar(null);
    setMostrarFormulario(true);
  };

  const handleEditarProyecto = (proyecto) => {
    setProyectoEditar(proyecto);
    setMostrarFormulario(true);
  };

  const handleEliminarProyecto = async (proyectoId) => {
    // Tu función handleEliminarProyecto permanece sin cambios
  };

  const handleActualizarProyectos = () => {
    getAllProject();
  };

  const handleCloseFormulario = () => {
    setMostrarFormulario(false);
  };

  const ProyectoCard = ({ proyecto }) => {
    // Tu componente ProyectoCard permanece sin cambios
  };

  return (
    <div className="container mx-auto px-4">
      {mostrarFormulario && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50">
          {/* Tu código para mostrar el formulario de creación/editar proyecto */}
        </div>
      )}

      <div className="max-w-screen-xl mx-auto p-6">
        <header className="flex justify-between items-center mb-8">
          {/* Tu código para el encabezado */}
        </header>

        <div className="grid grid-cols-1 gap-6 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4">
          {/* Utiliza clases de Tailwind CSS para definir el número de columnas */}
          {proyectos.map((proyecto) => (
            <ProyectoCard
              key={proyecto.id}
              proyecto={proyecto}
              onEditar={handleEditarProyecto}
              onEliminar={handleEliminarProyecto}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Main;
