import React, { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import Swal from 'sweetalert2';
import ProyectoCard from "./ProyectoCard";
import ProyectoForm from "./ProyectoForm";
import "./Main.css";
import { FaPlus } from "react-icons/fa";

const Main = () => {
  const [proyectos, setProyectos] = useState([]);
  const { register, handleSubmit, errors, reset } = useForm();

  useEffect(() => {
    getAllProjects();
  }, []);

  const getAllProjects = async () => {
    try {
      const response = await fetch("/projects");
      if (response.ok) {
        const data = await response.json();
        setProyectos(data);
      } else {
        throw new Error("Error al obtener los proyectos: " + response.statusText);
      }
    } catch (error) {
      console.error("Error al comunicarse con el servidor:", error);
      Swal.fire({
        title: 'Error',
        text: 'Hubo un problema al cargar los proyectos. Por favor, intenta nuevamente más tarde.',
        icon: 'error',
        confirmButtonText: 'Aceptar'
      });
    }
  };

  const handleCrearProyecto = async (data) => {
    try {
      const response = await fetch("/projects", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(data),
      });
      if (response.ok) {
        const nuevoProyecto = await response.json();
        setProyectos([...proyectos, nuevoProyecto]);
        Swal.fire({
          title: 'Proyecto creado',
          text: 'El proyecto ha sido creado exitosamente.',
          icon: 'success',
          confirmButtonText: 'Aceptar'
        });
        reset();
      } else {
        throw new Error("Error al crear el proyecto: " + response.statusText);
      }
    } catch (error) {
      console.error("Error al comunicarse con el servidor:", error);
      Swal.fire({
        title: 'Error',
        text: 'Hubo un problema al crear el proyecto. Por favor, intenta nuevamente más tarde.',
        icon: 'error',
        confirmButtonText: 'Aceptar'
      });
    }
  };

  const handleEliminarProyecto = async (proyectoId) => {
    try {
      const response = await fetch(`/projects/${proyectoId}`, {
        method: "DELETE"
      });
      if (response.ok) {
        setProyectos(proyectos.filter(proyecto => proyecto.id !== proyectoId));
        Swal.fire({
          title: 'Proyecto eliminado',
          text: 'El proyecto ha sido eliminado exitosamente.',
          icon: 'success',
          confirmButtonText: 'Aceptar'
        });
      } else {
        throw new Error("Error al eliminar el proyecto: " + response.statusText);
      }
    } catch (error) {
      console.error("Error al comunicarse con el servidor:", error);
      Swal.fire({
        title: 'Error',
        text: 'Hubo un problema al eliminar el proyecto. Por favor, intenta nuevamente más tarde.',
        icon: 'error',
        confirmButtonText: 'Aceptar'
      });
    }
  };

  return (
    <div className="main-container">
      <header className="header">
        <h2>Proyectos</h2>
        <button className="crear-proyecto-btn" onClick={handleSubmit(handleCrearProyecto)}>
          <FaPlus /> Crear Proyecto
        </button>
      </header>

      {proyectos.map((proyecto) => (
        <ProyectoCard key={proyecto.id} proyecto={proyecto} onDelete={handleEliminarProyecto} />
      ))}

      <ProyectoForm 
        register={register}
        handleSubmit={handleSubmit}
        errors={errors}
        reset={reset}
      />
    </div>
  );
};

export default Main;
