// Header.jsx
import React, { useState } from 'react';
import { useSearch } from './SearchContext';

function Header({ projects }) {
  const { searchTerm, updateSearchTerm, updateSearchResults } = useSearch();

  const handleSearchChange = (event) => {
    const term = event.target.value;
    updateSearchTerm(term);
    const filteredProjects = projects.filter((project) =>
      project.name.toLowerCase().includes(term.toLowerCase())
    );
    updateSearchResults(filteredProjects);
  };

  const handleSearchSubmit = (event) => {
    event.preventDefault();
    // Puedes manejar la búsqueda aquí si lo deseas
  };

  return (
    <nav className="navbar bg-base-100 shadow-lg fixed top-0 left-1 right-1">
      <div className="container mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16">
          {/* Logo */}
          <div className="flex-shrink-0 flex items-center">
            <a href="/login">
              <img className="h-12 w-15 rounded-full" src="https://raw.githubusercontent.com/raulgonca/Front_stafko/main/src/Image/Logito.svg" alt="Logo" />
            </a>
          </div>
          {/* Search */}
          <div className="flex-1 flex justify-center px-2 lg:ml-6 lg:justify-end">
            <form onSubmit={handleSearchSubmit} className="w-full max-w-lg">
              <label htmlFor="search" className="sr-only">Search</label>
              <div className="relative">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <svg className="h-5 w-5" fill="currentColor" viewBox="0 0 20 20">
                    <path fillRule="evenodd" d="M13.293 14.707a1 1 0 0 1-1.414 1.414l-3-3a1 1 0 1 1 1.414-1.414l3 3zM15 9a6 6 0 1 1-12 0 6 6 0 0 1 12 0z" clipRule="evenodd"/>
                  </svg>
                </div>
                <input
                  id="search"
                  className="block w-full bg-gray-900 focus:outline-none text-gray-100 placeholder-gray-400 rounded-md py-2 pl-10 pr-3 sm:text-sm"
                  style={{ backgroundColor: 'rgba(0, 0, 0, 0.3)' }}
                  placeholder="Search"
                  type="text"
                  value={searchTerm}
                  onChange={handleSearchChange}
                />
              </div>
            </form>
          </div>
          {/* Profile Dropdown */}
          <div className="flex items-center">
            <div className="relative ml-3">
              <div>
                <button className="max-w-xs bg-gray-800 rounded-full flex items-center text-sm focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-gray-800 focus:ring-white" id="user-menu" aria-haspopup="true">
                  <span className="sr-only">Open user menu</span>
                  <img className="h-8 w-8 rounded-full" src="" alt="Profile" />
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
      {/* Aquí puedes mostrar los resultados de la búsqueda */}
      {searchResults.length > 0 && (
        <div className="bg-gray-100 p-4">
          <h2 className="text-lg font-semibold mb-2">Search Results:</h2>
          <ul>
            {searchResults.map((project) => (
              <li key={project.id}>{project.name}</li>
            ))}
          </ul>
        </div>
      )}
    </nav>
  );
}

export default Header;
