import React, { useState } from "react";
import { FaEdit, FaTrash } from 'react-icons/fa';
import Swal from 'sweetalert2';
import ProyectoEdit from "./ProyectoEdit";

const ProyectoCard = ({ proyecto, onEditar, onEliminar }) => {
  const [mostrarDetalles, setMostrarDetalles] = useState(false);
  const [mostrarEditar, setMostrarEditar] = useState(false);

  const toggleDetalles = () => {
    setMostrarDetalles(!mostrarDetalles);
  };

  const handleEditar = () => {
    setMostrarEditar(true);
  };

  const handleCloseEditar = () => {
    setMostrarEditar(false);
  };

  const mostrarConfirmacionEliminar = async () => {
    const confirmacion = await Swal.fire({
      title: '¿Estás seguro?',
      text: 'Esta acción eliminará el proyecto permanentemente.',
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#3085d6',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Sí, eliminar',
      cancelButtonText: 'Cancelar'
    });

    if (confirmacion.isConfirmed) {
      onEliminar(proyecto.id);
    }
  };

  return (
    <div className="bg-white shadow-lg rounded-lg overflow-hidden">
      <div className="p-6 cursor-pointer" onClick={toggleDetalles}>
        <h3 className="text-lg font-semibold text-gray-800">{proyecto.nameproject}</h3>
        {mostrarDetalles && (
          <div>
            <p className="text-gray-600 mt-2">{proyecto.description}</p>
            <div className="flex mt-4">
              <button
                onClick={handleEditar}
                className="flex items-center px-3 py-1 bg-blue-500 text-white font-semibold rounded-md mr-2 transition duration-300 hover:bg-blue-600 focus:outline-none"
              >
                <FaEdit className="mr-1" /> Editar
              </button>
              <button
                onClick={mostrarConfirmacionEliminar}
                className="flex items-center px-3 py-1 bg-red-500 text-white font-semibold rounded-md transition duration-300 hover:bg-red-600 focus:outline-none"
              >
                <FaTrash className="mr-1" /> Eliminar
              </button>
            </div>
          </div>
        )}
      </div>
      {mostrarEditar && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-gray-100 w-96 p-4 rounded-lg shadow-lg relative z-50">
            <button
              onClick={handleCloseEditar}
              className="absolute top-2 right-2 text-gray-500 hover:text-gray-700 focus:outline-none"
            >
              <FaTimes />
            </button>
            <ProyectoEdit
              proyectoInicial={proyecto}
              onSubmit={handleCloseEditar}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default ProyectoCard;
