import React, { useState, useEffect } from "react";
import { FaPlus, FaEdit, FaTrash } from 'react-icons/fa'; // Importa los iconos de Font Awesome
import ProyectoForm from "./ProyectoForm";
import "./Main.css";

const Main = () => {
  const [proyectos, setProyectos] = useState([]);
  const [mostrarFormulario, setMostrarFormulario] = useState(false);

  useEffect(() => {
    getAllProject();
  }, []);

  const getAllProject = async () => {
    try {
      const response = await fetch("http://localhost:3000/projects/");
      if (response.ok) {
        const data = await response.json();
        setProyectos(data);
      } else {
        console.error("Error al obtener los proyectos:", response.statusText);
      }
    } catch (error) {
      console.error("Error al comunicarse con el servidor:", error);
    }
  };

  const handleCrearProyecto = async (proyecto) => {
    try {
      const response = await fetch("http://localhost:3000/projects/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(proyecto)
      });
      if (response.ok) {
        const proyectoCreado = await response.json();
        setProyectos([...proyectos, proyectoCreado]);
        toggleFormulario();
      } else {
        console.error("Error al guardar el proyecto:", response.statusText);
      }
    } catch (error) {
      console.error("Error al comunicarse con el servidor:", error);
    }
  };

  const toggleFormulario = () => {
    setMostrarFormulario(!mostrarFormulario);
  };

  return (
    <div className="main-container">
      <div className="header">
        <h2>Proyectos</h2>
        <button className="crear-proyecto-btn" onClick={toggleFormulario}>
          <FaPlus /> {/* Icono de más */}
        </button>
      </div>

      {mostrarFormulario && (
        <div className="ventana-emergente">
          <div className="contenido">
            <ProyectoForm onSubmit={handleCrearProyecto} />
          </div>
        </div>
      )}

      <ul className="proyectos-lista">
        {proyectos.map((proyecto, index) => (
          <li className="proyecto" key={index}>
            <div className="proyecto-info">
              <div><strong>Nombre:</strong> {proyecto.nameproject}</div>
              <div><strong>Descripción:</strong> {proyecto.description}</div>
              <div><strong>Fecha Inicio:</strong> {proyecto.fechaInicio}</div>
              <div><strong>Fecha Finalización:</strong> {proyecto.fechaFinalizacion}</div>
            </div>
            <div className="botones-proyecto">
              <button className="editar-btn" onClick={() => handleEditarProyecto(proyecto.id)}>
                <FaEdit /> {/* Icono de editar */}
              </button>
              <button className="eliminar-btn" onClick={() => handleEliminarProyecto(proyecto.id)}>
                <FaTrash /> {/* Icono de eliminar */}
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Main;
