import React, { useState, useEffect } from "react";
import ProyectoForm from "./ProyectoForm";
import ProyectoEdit from "./ProyectoEdit";
import "./Main.css";
import { FaEdit, FaTrash, FaPlus, FaTimes } from 'react-icons/fa';

const Main = () => {
  const [proyectos, setProyectos] = useState([]);
  const [mostrarFormulario, setMostrarFormulario] = useState(false);
  const [proyectoEditar, setProyectoEditar] = useState(null);

  useEffect(() => {
    getAllProject();
  }, []);

  const getAllProject = async () => {
    try {
      const response = await fetch("http://localhost:3000/projects/");
      if (response.ok) {
        const data = await response.json();
        setProyectos(data);
      } else {
        console.error("Error al obtener los proyectos:", response.statusText);
      }
    } catch (error) {
      console.error("Error al comunicarse con el servidor:", error);
    }
  };

  const handleCrearProyecto = () => {
    setProyectoEditar(null); // Limpiar cualquier proyecto de ediciÃ³n
    setMostrarFormulario(true);
  };

  const handleEditarProyecto = (proyecto) => {
    setProyectoEditar(proyecto);
    setMostrarFormulario(true);
  };

  const handleEliminarProyecto = async (proyectoId) => {
    try {
      const response = await fetch(`http://localhost:3000/projects/${proyectoId}`, {
        method: "DELETE"
      });
      if (response.ok) {
        const proyectosActualizados = proyectos.filter(proyecto => proyecto.id !== proyectoId);
        setProyectos(proyectosActualizados);
      } else {
        console.error("Error al eliminar el proyecto:", response.statusText);
      }
    } catch (error) {
      console.error("Error al comunicarse con el servidor:", error);
    }
  };

  const handleCloseFormulario = () => {
    setMostrarFormulario(false);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    // Realizar la solicitud PUT al servidor
    try {
        const response = await fetch(`http://localhost:3000/repo/${project.id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(proyectoEditar),
        });

        if (response.ok) {
            onSave(proyectoEditar);
            window.location.reload();
        } else {
            console.error('Error al actualizar el proyecto:', response.statusText);
        }
    } catch (error) {
        console.error('Error al actualizar el proyecto:', error);
    }
};

  return (
    <div className="main-container">
      <div className="header">
        <h2>Proyectos</h2>
        <button className="crear-proyecto-btn" onClick={handleCrearProyecto}>
          <FaPlus />
        </button>
      </div>

      {mostrarFormulario && (
        <div className="ventana-emergente">
          <div className="cerrar-ventana">
            {/* BotÃ³n de cerrar */}
            <button className="cerrar-btn" onClick={handleCloseFormulario}>
              <FaTimes />
            </button>
          </div>
          <div className="contenido">
            {proyectoEditar ? (
              <ProyectoEdit proyectoInicial={proyectoEditar} onSubmit={handleEditarProyectoSubmit} />
            ) : (
              <ProyectoForm onSubmit={handleCloseFormulario} />
            )}
          </div>
        </div>
      )}

      <ul className="proyectos-lista">
        {proyectos.map((proyecto, index) => (
          <li className="proyecto" key={index}>
            <div className="proyecto-info">
              <div><strong>Nombre:</strong> {proyecto.nameproject}</div>
              <div><strong>DescripciÃ³n:</strong> {proyecto.description}</div>
              <div><strong>Fecha Inicio:</strong> {proyecto.fechaInicio}</div>
              <div><strong>Fecha FinalizaciÃ³n:</strong> {proyecto.fechaFinalizacion}</div>
            </div>
            <div className="botones-proyecto">
              <button className="editar-btn" onClick={() => handleEditarProyecto(proyecto)}>
                <FaEdit />
              </button>
              <button className="eliminar-btn" onClick={() => handleEliminarProyecto(proyecto.id)}>
                <FaTrash />
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Main;
