import React, { useState } from 'react';

function Header() {
  const [darkMode, setDarkMode] = useState(false);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  const toggleMode = () => {
    setDarkMode(!darkMode);
  };

  return (
    <nav className={`navbar bg-base-100 shadow-lg fixed top-0 left-0 right-0 z-50 ${darkMode ? 'bg-[#383838]' : 'bg-white'}`}>
      <div className="container mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16 items-center">
          {/* Logo */}
          <div className="flex-shrink-0 flex items-center">
            <a href="/login">
              <img className="h-15 w-16 max-w-16" src="https://raw.githubusercontent.com/raulgonca/Front_stafko/main/src/Image/Logito.svg" alt="Logo" />
            </a>
          </div>
          {/* Toggle Button */}
          <label className="relative inline-flex items-center cursor-pointer" htmlFor="toggleButton">
            <input
              id="toggleButton"
              className="sr-only peer"
              value=""
              type="checkbox"
              checked={darkMode}
              onChange={toggleMode}
            />
            <div
              className={`w-24 h-12 rounded-full ring-0 peer duration-500 outline-none bg-gray-200 overflow-hidden before:flex before:items-center before:justify-center after:flex after:items-center after:justify-center before:content-['☀️'] before:absolute before:h-10 before:w-10 before:top-1/2 before:bg-white before:rounded-full before:left-1 before:-translate-y-1/2 before:transition-all before:duration-700 peer-checked:before:opacity-0 peer-checked:before:rotate-90 peer-checked:before:-translate-y-full shadow-lg shadow-gray-400 peer-checked:shadow-lg peer-checked:shadow-gray-700 peer-checked:bg-[#383838] after:content-['🌑'] after:absolute after:bg-[#1d1d1d] after:rounded-full after:top-[4px] after:right-1 after:translate-y-full after:w-10 after:h-10 after:opacity-0 after:transition-all after:duration-700 peer-checked:after:opacity-100 peer-checked:after:rotate-180 peer-checked:after:translate-y-0 ${darkMode ? 'peer-checked:bg-[#383838]' : 'peer-checked:bg-[#1d1d1d]'}`}
            ></div>
          </label>
          {/* Profile Dropdown */}
          <div className="flex items-center">
            <div className="relative ml-3">
              <p className="max-w-xs bg-gray-800 mt-1 rounded-full flex items-center text-sm focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-gray-800 focus:ring-white" id="user-menu" aria-haspopup="true">
                <img className="h-8 w-8 rounded-full" src="https://cdn.icon-icons.com/icons2/1508/PNG/512/systemusers_104569.png" alt="Profile" />
              </p>
            </div>
          </div>
        </div>
      </div>
    </nav>
  );
}

export default Header;
