import React, { useState, useEffect } from "react";
import "./ProyectoEdit.css";

const EditarProyectoForm = ({ proyectoInicial, onSubmit }) => {
  const [proyecto, setProyecto] = useState({
    id: null,
    nameproject: "",
    description: "",
    fechaInicio: "",
    fechaFinalizacion: ""
  });

  // Actualiza el estado del proyecto cuando proyectoInicial cambia
  useEffect(() => {
    if (proyectoInicial) {
      setProyecto(proyectoInicial);
    }
  }, [proyectoInicial]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProyecto({
      ...proyecto,
      [name]: value
    });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    // Realizar la solicitud PUT al servidor
    try {
        const response = await fetch(`http://localhost:3000/repo/${project.id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            // eslint-disable-next-line no-undef
            body: JSON.stringify(editedProject),
        });

        if (response.ok) {
            onSave(editedProject);
            window.location.reload();
        } else {
            console.error('Error al actualizar el proyecto:', response.statusText);
        }
    } catch (error) {
        console.error('Error al actualizar el proyecto:', error);
    }
};

  return (
    <div>
      <h2>Editar Proyecto</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Nombre del Proyecto:</label>
          <input
            type="text"
            name="nameproject"
            value={proyecto.nameproject}
            onChange={handleChange}
            placeholder="Ingrese el nombre del proyecto aquí"
          />
        </div>
        <div className="form-group">
          <label>Descripción:</label>
          <textarea
            name="description"
            value={proyecto.description}
            onChange={handleChange}
            placeholder="Descripción del proyecto"
          />
        </div>
        <div className="form-group">
          <label>Fecha de Inicio:</label>
          <input
            type="date"
            name="fechaInicio"
            value={proyecto.fechaInicio}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label>Fecha de Finalización:</label>
          <input
            type="date"
            name="fechaFinalizacion"
            value={proyecto.fechaFinalizacion}
            onChange={handleChange}
          />
        </div>
        <button type="submit">Guardar Cambios</button>
      </form>
    </div>
  );
};

export default EditarProyectoForm;
