import React, { useState, useEffect } from "react";
import Swal from 'sweetalert2';
import { FaPlus, FaTimes } from 'react-icons/fa';
import ProyectoForm from "./ProyectoForm";
import ProyectoEdit from "./ProyectoEdit";
import ProyectoCard from "./ProyectoCard";

const Main = () => {
  const [proyectos, setProyectos] = useState([]);
  const [mostrarFormulario, setMostrarFormulario] = useState(false);
  const [proyectoEditar, setProyectoEditar] = useState(null);

  useEffect(() => {
    obtenerProyectos();
  },);

  const obtenerProyectos = async () => {
    try {
      const response = await fetch("http://localhost:3000/projects/");
      if (response.ok) {
        const data = await response.json();
        setProyectos(data);
      } else {
        throw new Error("Error al obtener los proyectos: " + response.statusText);
      }
    } catch (error) {
      console.error("Error al comunicarse con el servidor:", error);
      mostrarError("Hubo un problema al cargar los proyectos. Por favor, intenta nuevamente más tarde.");
    }
  };

  const handleCrearProyecto = () => {
    setProyectoEditar(null);
    setMostrarFormulario(true);
  };

  const handleEditarProyecto = (proyecto) => {
    setProyectoEditar(proyecto);
    setMostrarFormulario(true);
  };

  const handleEliminarProyecto = async (proyectoId) => {
    // Aquí puedes implementar la lógica para eliminar un proyecto
  };

  const handleCloseFormulario = () => {
    setMostrarFormulario(false);
    obtenerProyectos();
  };

  const mostrarError = (mensaje) => {
    Swal.fire({
      title: 'Error',
      text: mensaje,
      icon: 'error',
      confirmButtonText: 'Aceptar'
    });
  };

  return (
    <div className="bg-gradient-to-b from-blue-900 to-blue-700 min-h-screen">
      {mostrarFormulario && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white w-96 p-6 rounded-lg shadow-lg relative z-50">
            <button
              onClick={handleCloseFormulario}
              className="absolute top-2 right-2 text-gray-500 hover:text-gray-700 focus:outline-none"
            >
              <FaTimes />
            </button>
            <div className="form-container">
              {proyectoEditar ? (
                <ProyectoEdit
                  proyectoInicial={proyectoEditar}
                  onSubmit={handleCloseFormulario}
                />
              ) : (
                <ProyectoForm
                  onSubmit={handleCloseFormulario}
                />
              )}
            </div>
          </div>
        </div>
      )}

      <div className="max-w-4xl mx-auto p-6">
        <header className="flex justify-between items-center mb-8">
          <h2 className="text-4xl font-bold text-white">Welcome to Your Projects</h2>
          <button
            onClick={handleCrearProyecto}
            className="flex items-center px-6 py-3 bg-blue-600 text-white font-semibold rounded-lg shadow-lg transition duration-300 hover:bg-blue-700 hover:scale-105 focus:outline-none"
          >
            <FaPlus className="mr-2" /> New Project
          </button>
        </header>

        <div className="grid grid-cols-1 gap-6 md:grid-cols-2 lg:grid-cols-3">
          {proyectos.map((proyecto) => (
            <ProyectoCard
              key={proyecto.id}
              proyecto={proyecto}
              onEditar={handleEditarProyecto}
              onEliminar={handleEliminarProyecto}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Main;
